import styled from 'styled-components';
import { Link as LinkRouter } from 'react-router-dom';
import { Link as LinkScroll } from 'react-scroll';

export const Container = styled.div`
    display: flex;
    /* margin: 1rem; */
    width: 100vw;
    height: 100vh;
    /* top: 6.6vh; */
    /* overflow-y: scroll;

    ::-webkit-scrollbar {
        display: none;
    } */

    @media screen and (max-width: 768px) {
        margin-left: 16px;
    }
`

export const Header = styled.div`
    position: fixed;
    top: 9vh;
    z-index: 999;
    text-decoration: none;

    @media screen and (max-width: 768px) {
        top: -.6rem;
        z-index: 9;
        /* background: white; */
        padding-top: 5rem;
        padding-bottom: 1.25rem;
        width: 100%;
        overflow-x: hidden;
    }
`

export const Back = styled(LinkRouter)`
    position: fixed;
    /* width: 6.5rem; */
    height: 2.8vw;
    /* margin-left: 50.8vw; */
    margin-left: 1.6vw;
    margin-top: .35vh;
    /* padding-left: .5rem; */
    /* padding-right: .5rem; */
    padding: .4vw;
    padding-right: 1.1vw;
    padding-left: 1.1vw;

    border: 1px solid black;
    border-radius: 70px;
    font-size: 1.7vw;

    color: black;
    background: white;
    text-decoration: none;

    &:hover {
        transition: all 0.2s ease-in-out;
        background: black;
        color: white;
    }

    @media screen and (max-width: 768px) {
        /* margin-left: -.255px; */
        height: 5vh;
        margin-top: -.4vh;
        margin-left: 8px;
        padding: .75vh;
        padding-right: 2vw;
        padding-left: 2vw;
        font-size: 3vh;
    }
`

export const Content = styled.div`
    /* padding-left: 50.8vw; */
    /* margin-left: 3vw; */
    width: 49vw;
    position: relative;
    overflow-wrap: word-break;

    @media screen and (max-width: 768px) {
        display: grid;
        padding-left: 0;
        padding-top: 4rem;
        width: 96vw;
        padding-bottom: 4rem;
        /* font-size: 3.5rem; */
        /* overflow-wrap: word-break; */
        /* overflow-x: hidden; */
    }
`

export const FixedContent = styled.div`
    /* left: 50.8vw; */
    position: fixed;
    top: 7.75vh;
    left: 14.4vw;
    width: 33.6vw;
    /* background: white; */

    @media screen and (max-width: 768px) {
        top: 8vh;
        left: 1.5vw;
        width: 97vw;
        position: relative;
        /* overflow-x: hidden; */
    }
`

export const Title = styled.h1`
    /* width: 30%; */
    font-size: 5vw;
    position: relative;
    text-decoration: none;
    font-weight: bold;
    color: black;
    line-height: 88%;
    letter-spacing: -1px;
    margin-top: 1rem;

    @media screen and (max-width: 768px) {
        margin-top: 0rem;
        font-size: 4rem;
        position: relative;
    }
`

export const TagsContainer = styled.div`
    position: relative;
    margin-top: 1.25rem;

    @media screen and (max-width: 768px) {
        margin-top: 1rem;   
        position: relative;    
    }
`

export const Tag = styled.div`
    height: 100%;
    width: 5rem;
    display: inline;
    background-color: white;
    float: none;
    border: 1px solid black;
    border-radius: 70px;
    font-size: 1rem;
    padding: .25rem;
    padding-left: .5rem;
    padding-right: .5rem;
    margin-right: .5rem;
    margin-left: .1vw;

    @media screen and (max-width: 768px) {
        padding-left: .5rem;       
    }
`

export const Details = styled.div`
    padding-top: 4vh;
    padding-left: .2vw;
    /* position: fixed; */

    @media screen and (max-width: 768px) {
        padding-top: 1rem;
        padding-left: .5vw;
        width: 94%;
    }
`

export const Images = styled.div`
    position: relative;
    left: 51vw;
    top: 9vh;
    width: 38vw;
    padding-bottom: .5rem;

    @media screen and (max-width: 768px) {
        position: relative;
        left: 9.5px;
        top: 10vh;
        display: grid;
        width: 89.5vw;
        padding-bottom: 3rem;
    }
`

export const PicContainer = styled.div`
    margin-bottom: 1.5rem;
`

export const ThumbnailSelector = styled.div`
    z-index: 999;
    position: fixed;
    width: 6.5vw;
    height: 4vh;
    /* top: ${props => (props.level)}; */
    top: 48vh;
    right: 2.25vw;
    border: 1px solid black;

    @media screen and (max-width: 768px) {
        display: none;
    }
`

export const Thumbnails = styled.div`
    position: fixed;
    right: 3vw;
    top: 32vh;
    display: grid;
    width: 5vw;
    height: 36vh;
    z-index: 999;
    cursor: default;
    text-align: center;

    /* background: grey; */
    opacity: 0.7;

    @media screen and (max-width: 768px) {
        display: none;
    }
`

export const PicScroll = styled(LinkScroll)`
    /* position: relative; */
    height: ${props => (props.relHeight)};
    width: 100%;
    /* cursor: pointer; */
    margin-top: .25vh;
    margin-bottom: .25vh;
`

export const Top = styled(LinkScroll)`
    display: none;

    @media screen and (max-width: 768px) {
        position: fixed;
        display: flex;
        z-index: 990;
        /* cursor: pointer; */
        font-size: 1.5rem;
        background: white;
        color: black;
        text-decoration: none;

        bottom: 8vh;
        right: 25px;
        width: 5rem;
        height: 5rem;
        padding-left: 1rem;
        padding-top: 1.6rem;
        border-radius: 2.5rem;
        border: 1px solid black;

        /* left: 45vw;
        width: 20vw;
        height: 5vh;
        bottom: 8vh;
        padding-left: 1.5rem;
        padding-top: .5rem; */
    }

    &:hover {
        background: black;
        color: white;        
    }
`

export const NavButtons = styled.div`
    z-index: 999;
    position: fixed;
    border: 1.5px solid black;
    width: 6vw;
    height: 3vw;
    left: 47vw;
    bottom: 2vh;
    padding: 0;
`

export const PrevWork = styled(LinkRouter)`
    width: 3vw;
    height: 3vw;
    font-size: 3vw;
    border-right: 1.5px solid black;
    text-decoration: none;
    /* opacity: ${({ first }) => (first ? '0.4' : '1')}; */
    /* pointer-events: ${({ first }) => (first ? 'none' : 'auto')}; */
`

export const NextWork = styled(LinkRouter)`
    width: 3vw;
    height: 3vw;
    font-size: 3vw;
    text-decoration: none;
    /* opacity: ${({isLast}) => (isLast ? '0.4' : '1')}; */
    /* pointer-events: ${({ isLast }) => (isLast ? 'none' : 'auto')}; */
`
