import React, { useState, useEffect } from 'react';
import { FaBars } from 'react-icons/fa';
import { IconContext } from 'react-icons/lib';
import { Nav, NavbarContainer, NavLogo, MobileIcon, NavMenu, NavItem, NavLinksDOM, NavLinks, NavBtn, NavBtnLink } from './NavbarElements';

const Navbar = ({toggle}) => {
    const [scrollNav, setScrollNav] = useState(false);

    //TODO triggers something when certain scroll level has been reached
    const changeNav = () => {
        if(window.scrollY >= 80) {
            setScrollNav(true);
        } else {
            setScrollNav(false);
        }
    };

    useEffect(() => {
        window.addEventListener('scroll', changeNav)
    }, []);

    return (
        <Container showInfo={toggle}
            as={motion.div} initial={{ x: '-100vw', y: '0' }} 
            animate={toggle ? {  x: '0', y: 0 } : { x: '-100vw', y: '0' }}
            transition={{
            type: "tween",
            ease: [0.28, 1.35, 1.5, .91],
            duration: 0.6
            }}
        >
            <IconContext.Provider value={{ color: '#fff '}}>
                <Nav scrollNav={scrollNav}>
                    <NavbarContainer>
                        {/* <NavLogo to="/">N</NavLogo> */}
                        <NavMenu>
                            <NavItem>
                                <NavLinksDOM to="/main">Landing</NavLinksDOM>
                            </NavItem>
                            <NavItem>
                                <NavLinks to="work">Work</NavLinks>
                            </NavItem>
                            <NavItem>
                                <NavLinks to="nektine">Nektine</NavLinks>
                            </NavItem>
                            <NavItem>
                                <NavLinks to="about">About</NavLinks>
                            </NavItem>
                        </NavMenu>
                    </NavbarContainer>
                </Nav>
            </IconContext.Provider>
        </Container>
    );
};

export default Navbar;
