// import React, { useState, useEffect } from 'react';
import { motion } from "framer-motion";
import { Container, DarkWipe, LightWipe } from './WipeElements';

// const lightTheme = {
//     bg: "rgba(245, 251, 255, 1)",
//     bgBlur: "rgba(220, 212, 230, 0.6)",
//     bgSide: "rgba(241, 242, 246, 0.8)",
//     bgNav: "rgba(242, 249, 256, 0.65)",
//     bgPreview: "rgba(255, 255, 255, 0.7)",
//     primary: "#000000",
//     secondary: "#777777",
//     cs: "#0047FD",
//     border: "#E4E4E4"
//   };

//   const darkTheme = {
//     bg: "black",
//     bgBlur: "rgba(100, 100, 100, 0.35)",
//     bgSide: "rgba(51, 49, 52, 0.8)",
//     bgNav: "rgba(30, 30, 31, 0.65)",
//     bgPreview: "rgba(8, 8, 8, 0.85)",
//     primary: "#FFFFFF",
//     secondary: "#BABABA",
//     border: "#403F3F",
//     cs: "#5685FF",
//   }


const Wipe = ({ themeToggleStatus }) => {
    return (
        <Container>
            <DarkWipe
                as={motion.div} 
                initial={{ opacity: 0, y: '0vh' }} 
                animate={(themeToggleStatus == 'DarkTheme') ? {  opacity: 0, y: '-100vh' } : { opacity: 1, y: '0vh' }}
                transition={{
                type: "tween",
                ease: [0.28, 1.35, 1.5, .91],
                duration: 0.3
                }}
            >
            </DarkWipe>
            <LightWipe
                as={motion.div} 
                initial={{ opacity: 1, y: '0vh' }} 
                animate={(themeToggleStatus == 'lightTheme') ? {  opacity: 0, y: '-100vh' } : { opacity: 1, y: '0vh' }}
                transition={{
                type: "tween",
                ease: [0.28, 1.35, 1.5, .91],
                duration: 0.3
                }}
            >
            </LightWipe>
        </Container>
    );
};

export default Wipe;
