import "./App.scss";
import React, { useState } from 'react';
import { BrowserRouter as Router, Switch, Route, useLocation } from 'react-router-dom';
import GlobalFonts from './fonts/fonts';
import { motion, AnimatePresence } from "framer-motion";

import Home from "./components/Home";
import Main from "./components/Main";
import Navigation from "./components/Navigation";
import Status from "./components/Status";
import MobileBar from "./components/MobileBar";
import { Button2 } from "./components/ButtonElement";
import DarkModeButton from "./components/DarkModeButton";
import Works from "./components/Works";
import WorkExpanded from "./components/Works/WorkExpanded"
import About from "./components/About";
import Topbar from "./components/Topbar";
import Lab from "./components/Lab";
import Close from "./components/About";
import MobileToggle from "./components/MobileToggle";
import WIP from "./components/WIP";

//Our App Components
function App() {

  const [isOpen, setIsOpen] = useState(true);

  const toggle = () => {
    setIsOpen(!isOpen);
  };

  const [aboutToggle, setAboutToggle] = useState(false);

  const toggleAbout = (currPage) => {
    console.log("about toggled");
    if(currPage == 'info') {
      setAboutToggle(!aboutToggle);
    } else {
      setAboutToggle(false);
    }
  }

  // const location = useLocation();
  //TODO: make 2 Styled Component Themes, declare state that is one Theme.
  //----- ON dark/mode press, change state to other theme. Pass in state to each component.

  const pageTransition = {
    type: "tween",
    ease: [0.7, 0.1, 0.4, 1],
    duration: 0.5
  };

  return (
    <Router>
        <GlobalFonts />
        {/* <MobileBar isOpen={isOpen} toggle={toggle} /> */}
        {/* <MobileToggle isOpen={isOpen} toggle={toggle} /> */}
        <Navigation toggle={toggleAbout} aboutToggle={aboutToggle}/>
        <About aboutToggle={aboutToggle} toggle={toggleAbout}><Close onClick={toggleAbout}>close</Close></About>
        {/* <About as={motion.div} initial={{ x: '20.5rem', y: '105vh' }} 
                animate={aboutToggle ? {  x: '20.5rem', y: -18 } : { x: '20.5rem', y: '-105vh' }}
                transition={pageTransition} showInfo={!aboutToggle}></About> */}
        <AnimatePresence exitBeforeEnter>
          <Switch location={window.location} key={window.location.pathname}>
          {/* <Switch> */}
            <Route path="/" exact component={Home} />
            <Route path="/works" exact>
              <Works aboutToggle={aboutToggle}/>
            </Route>
            <Route path="/:workId?/works"component={WorkExpanded} />
            <Route path="/main" exact component={Main} />
            <Route path="/lab" exact component={Lab} />
            {/* <Route path='/info' exact component={About} /> */}
          </Switch>
        </AnimatePresence>
        <Status />
        <WIP />
      {/* <DarkModeButton /> */}
    </Router>
  );
}

export default App;
