import React, { useState, useEffect } from 'react';
import { Container, Nav, NavbarContainer, NavLogo, About, MobileAboutContainer, Logo, NavHome, Close, NavMenu, NavItem, NavLinksDOM, NavLinks, CloseBtn, NavBtnLink } from './NavbarElements';
import { motion } from "framer-motion";

const Navbar = ({mobileToggle, isOpen, toggleAbout, aboutToggle}) => {

    const [mobileAbout, setMobileAbout] = useState(false);

    const toggleMobileAbout = () => {
        console.log("mobile about toggled", mobileAbout);
        setMobileAbout(!setMobileAbout);
      };

    return (
        <Container
            as={motion.div} initial={{ x: 0, y: '321px' }} 
            animate={isOpen ? {  x: 0, y: 0 } : { x: 0, y: '321px' }}
            transition={{
            type: "tween",
            ease: [0.28, 1.35, 1.5, .91],
            duration: 0.3
            }}
        >
            <NavMenu
                as={motion.div} initial={{ x: '0vw', y: 0 }} 
                animate={mobileAbout ? {  x: '-100vw', y: 0 } : { x: '0vw', y: 0 }}
                transition={{
                type: "tween",
                ease: [0.28, 1.35, 1.5, .91],
                duration: 0.3
                }}
            >
                <NavHome onClick={mobileToggle} to="/works"><Logo /><a style={{ marginLeft: '8px', marginTop: '1px' }}>호연</a></NavHome>
                <NavLinksDOM onClick={mobileToggle} to="/works">Work</NavLinksDOM>
                <NavLinksDOM onClick={mobileToggle} to="/test">The Lab</NavLinksDOM>
                <NavLinksDOM onClick={mobileToggle} to="">Photography</NavLinksDOM>
                <NavLinksDOM onClick={mobileToggle} to="/blog">Lab</NavLinksDOM>
            </NavMenu>
            <MobileAboutContainer
                as={motion.div} initial={{ x: '100vw', y: 0 }} 
                animate={mobileAbout ? {  x: '0vw', y: 0 } : { x: '100vw', y: 0 }}
                transition={{
                type: "tween",
                ease: [0.28, 1.35, 1.5, .91],
                duration: 0.3
                }}
            >
                <h3>Austin Kim</h3>
            </MobileAboutContainer>
            <Close onClick={mobileToggle}>Close</Close>
            <About onClick={toggleMobileAbout} aboutToggled={mobileAbout}>About/Contact</About>
            {/* <About currpage={aboutToggle} thispage={true} onClick={() => toggleAbout('info')}>About/Contact</About> */}
            <CloseBtn onClick={mobileToggle}>〤</CloseBtn>
        </Container>
    );
};

export default Navbar;
