import React, { useState, useEffect } from 'react';
import { connect } from 'react-redux';
import { ThemeProvider } from "styled-components";
import { motion } from "framer-motion";

import { Container, BodySection, WorksImageContainer, BasicTxt1, BasicTxt2, SelectedLabel, Divider, Divider2, MainBio, Name, Kim, DownButton, FilterPreview, FilterContent, SDTypeHover, SDContent, UXTypeHover, UXContent, TypeHover, Marquee, ImageContainer, Styled, HoverName, ArrowAnim, SelectedWorks, WorkItem, ItemIndex, ItemDetail, SelectedListContainer, ButtonContainer } from './MainElements';

const Main = ({projects}) => {

    const [scrollName, setScrollName] = useState(false);
    const [colorThemeInvert, setColorThemeInvert] = useState(false);

    const [topVal, setTopVal] = useState("-1px");
    const [leftVal, setLeftVal] = useState("-1px");

    let topWorks = projects.map((project) => {
        var zerofilled = ('0'+project.id).slice(-2);

        if (project.id == 2 ||
            project.id == 4 ||
            project.id == 5 ||
            project.id == 7 ||
            project.id == 10 ||
            project.id == 12) {
            return (
                <WorkItem>
                    <ItemDetail to={"/works"}>
                        {project.name}
                        {/* {project.imgs[0]} */}
                        <WorksImageContainer topVal={topVal} leftVal={leftVal}> 
                                <img src={"imgs/" + project.imgs[0]} onerror='this.style.display = "none"' alt={'picture'} style={{objectFit: 'contain', justifyContent: 'center', maxWidth: '98%'}}></img>
                        </WorksImageContainer>
                    </ItemDetail>
                    {/* <ItemIndex>{zerofilled}</ItemIndex> */}
                </WorkItem>
            );
        } else {
            return (
                <></>
            );
        }

    });

    // 100 - 4.8 = 93.2
    // 93.2 / length = * .7
    // console.log(projects.length);
    // TODO: pass in Projects Length as variable instaed of Connecting to Store to retrieve
    // let margin = (64 / projects.length) * .8;
    // let y_index = ((project.id - 1) * margin) + 49;
    // let stringY = y_index + "vh";
    // let stringY2 = (y_index - 2) + "vh";
    // console.log("stringY2: " + stringY2);

    document.onmousemove = function(event) {
        // pointerX = event.screenX + "px";
        // pointerY = event.screenY + "px";
        if (event.pageY >= (window.innerHeight - 340)) {
            setTopVal((event.pageY - 340) + "px");
            setLeftVal((event.pageX + 2) + "px");
        } else {
            setTopVal((event.pageY + 1) + "px");
            setLeftVal((event.pageX + 2) + "px");
        }
    } 

    const changeName = () => {
        if(window.scrollY >= 60) {
            setScrollName(true);
        } else {
            setScrollName(false);
        }
    };

    useEffect(() => {
        window.addEventListener('scroll', changeName)
    }, []);

    const graphic_projects = projects.filter(project => project.type == 1);
    let graphic_images = graphic_projects.map((project) => (
        <ImageContainer>
            <img src={"imgs/" + project.imgs[0]} alt={'picture'} style={{objectFit: 'contain', justifyContent: 'center'}} id="cover"></img>
        </ImageContainer>
    ));


    const sd_projects = projects.filter(project => project.type == 2);
    let sd_images = sd_projects.map((project) => (
        <ImageContainer>
            <img src={"imgs/" + project.imgs[0]} onerror='this.style.display = "none"' alt={'picture'} style={{objectFit: 'contain', justifyContent: 'center'}} id="cover"></img>
        </ImageContainer>
    ));

    // console.log(sd_images);

    const ux_projects = projects.filter(project => project.type == 3);
    // let ux_images = ux_projects.map((project) => (
    //     <ImageContainer>
    //         <img src={"imgs/" + project.imgs[0]} onerror='this.style.display = "none"' alt={'picture'} style={{width: '100%'}} id="cover"></img>
    //     </ImageContainer>
    // ));

    // Button.defaultProps = {
    //     theme: {
    //       main: "palevioletred"
    //     }
    //   }

    const theme = {
        gdLength: graphic_projects.length * -26,
        sdLength: sd_projects.length,
        uxLength: ux_projects.length
    };

    const pageVariants = {
        initial: {
          y: '0',
          x: '-100vw'
        },
        in: {
          y: '0vh',
          x: 0
        },
        out: {
          y: '0vh',
          x: '-100vw'
        }
    }

    const rightVariants = {
        initial: {
          y: '0',
          x: '100vw'
        },
        in: {
          y: '0vh',
          x: 'calc(50vw + .5rem)'
        },
        out: {
          y: '0vh',
          x: '100vw'
        }
    }

    const bottomTransition = {
        initial: {
          y: '60vh',
          x: '3.5rem'
        },
        in: {
          y: '0vh',
          x: '3.5rem'
        },
        out: {
          y: '60vh',
          x: '3.5rem'
        }
    }

    const pageTransition = {
        type: "tween",
        ease: [0.87, 0, 0.13, 1],
        duration: 0.7
    };

    return (
        <Container>
            <MainBio zVal={999}
            as={motion.div} initial="initial"
            animate="in"
            exit="out"
            variants={pageVariants}
            transition={pageTransition}
            >
                <BasicTxt1>
                    Austin Kim <TypeHover>
                        <HoverName>
                            (호연) <img src={"imgs/me3.jpeg"} onerror='this.style.display = "none"' alt={'picture'} id="cover"></img>
                        </HoverName>
                        <ImageContainer topVal={topVal} leftVal={leftVal}>
                            <img src={"imgs/me3.jpeg"} onerror='this.style.display = "none"' alt={'picture'} style={{objectFit: 'contain', justifyContent: 'center', maxWidth: '97.5%', maxHeight: '92.5%'}} id="cover"></img>
                        </ImageContainer>    
                    </TypeHover> is a creative based in <TypeHover><HoverName>ATL / BOS <img src={"imgs/bos.jpeg"} onerror='this.style.display = "none"' alt={'picture'} id="cover"/></HoverName> 
                        <ImageContainer topVal={topVal} leftVal={leftVal}>
                            <img src={"imgs/bos.jpeg"} onerror='this.style.display = "none"' alt={'picture'} style={{objectFit: 'contain', justifyContent: 'center', maxWidth: '97.5%', maxHeight: '92.5%'}} id="cover"></img>
                        </ImageContainer>   
                    </TypeHover>, passionate about <TypeHover isBg={false}><HoverName>graphic design <img src={"imgs/Lab/2_1.png"} onerror='this.style.display = "none"' alt={'picture'} id="cover"></img>, </HoverName>
                        <ImageContainer topVal={topVal} leftVal={leftVal}> 
                            <img src={"imgs/Lab/2_1.png"} onerror='this.style.display = "none"' alt={'picture'} style={{objectFit: 'contain', justifyContent: 'center', maxWidth: '97.5%', maxHeight: '92.5%'}} id="cover"></img>
                        </ImageContainer>
                    </TypeHover>
                    <TypeHover isBg={false}><HoverName>web design <img src={"imgs/Website/web2.png"} onerror='this.style.display = "none"' alt={'picture'} id="cover"></img>,</HoverName>
                        <ImageContainer topVal={topVal} leftVal={leftVal}>
                            <img src={"imgs/Website/web2.png"} onerror='this.style.display = "none"' alt={'picture'} style={{objectFit: 'contain', justifyContent: 'center', maxWidth: '97.5%', maxHeight: '92.5%'}} id="cover"></img>
                        </ImageContainer>
                    </TypeHover>
                    <TypeHover isBg={false}><HoverName>experience design <img src={"imgs/Cap/7.jpg"} onerror='this.style.display = "none"' alt={'picture'} id="cover"></img>,</HoverName>
                        <ImageContainer topVal={topVal} leftVal={leftVal}>
                            <img src={"imgs/Cap/7.jpg"} onerror='this.style.display = "none"' alt={'picture'} style={{objectFit: 'contain', justifyContent: 'center', maxWidth: '97.5%', maxHeight: '92.5%'}} id="cover"></img>
                        </ImageContainer>
                    </TypeHover> and <TypeHover isBg={false}><HoverName>photography <img src={"imgs//Film/1_3.jpeg"} onerror='this.style.display = "none"' alt={'picture'} id="cover"></img>.</HoverName>
                        <ImageContainer topVal={topVal} leftVal={leftVal}>
                            <img src={"imgs//Film/1_3.jpeg"} onerror='this.style.display = "none"' alt={'picture'} style={{objectFit: 'contain', justifyContent: 'center', maxWidth: '97.5%', maxHeight: '92.5%'}} id="cover"></img>
                        </ImageContainer>
                    </TypeHover>
                </BasicTxt1>
                <BasicTxt2>
                    Austin Kim <TypeHover>
                        <HoverName>
                            (호연)
                        </HoverName>
                    </TypeHover> is a creative based in <TypeHover><HoverName>ATL / BOS </HoverName> 
                    </TypeHover>, passionate about <TypeHover isBg={false}><HoverName>graphic design, </HoverName>
                    </TypeHover>
                    <TypeHover isBg={false}><HoverName>web design,</HoverName>
                    </TypeHover>
                    <TypeHover isBg={false}><HoverName>experience design,</HoverName>
                    </TypeHover> and <TypeHover isBg={false}><HoverName>photography.</HoverName>
                    </TypeHover>
                </BasicTxt2>
            </MainBio>
            <Divider
            as={motion.div} initial="initial"
            animate="in"
            exit="out"
            variants={rightVariants}
            transition={pageTransition}/>
            {/* <Divider2 /> */}
            {/* <MainBio zVal={-1}>
                <BasicTxt>Austin Kim 
                    <TypeHover>(í˜¸ì—°)
                    </TypeHover>
                </BasicTxt> 
                <BasicTxt>is a creative based in 
                    <TypeHover> ATL+BOS, 
                    </TypeHover>
                </BasicTxt> 
                <BasicTxt>passionate about 
                <TypeHover isBg={true}> graphic design
                </TypeHover>, 
                <TypeHover isBg={true}> web design
                </TypeHover>, 
                <TypeHover isBg={true}> experience design
                </TypeHover>, and 
                <TypeHover isBg={true}> photography
                </TypeHover>.
                </BasicTxt> 
            </MainBio> */}
            <ButtonContainer
                as={motion.div} initial={{ y: '20vh'}}
                animate={{ y: '0'}}
                exit={{ y: '20vh'}}
                transition={{type: "tween",
                ease: [0.87, 0, 0.13, 1],
                duration: 1.2}}
            >
                <DownButton to={"/works"}>Go to works</DownButton>
            </ButtonContainer>
            {/* <SelectedLabel zVal={999}
                // as={motion.div} initial="initial"
                // animate="in"
                // exit="out"
                // variants={bottomTransition}
                // transition={pageTransition}
            >
                Selected Works
            </SelectedLabel>
            <SelectedWorks zVal={999}
                // as={motion.div} initial="initial"
                // animate="in"
                // exit="out"
                // variants={bottomTransition}
                // transition={pageTransition}
            >
                    <SelectedListContainer>
                        {topWorks}
                    </SelectedListContainer>
            </SelectedWorks> */}
        </Container>
    );
};

export default connect(({projects}) => ({projects}))(Main);
