// import React, { useState, useEffect } from 'react';
import { motion } from "framer-motion";
import { Container, Overlay } from './CloseOverlayElements';

const CloseOverlay = ({isOpen, aboutToggle}) => {


    return (
        <Container>
            <Overlay 
                showInfo={isOpen}
                as={motion.div} initial={{ display: 'none', opacity: 0 }} 
                animate={isOpen ? { display: 'flex', opacity: 1  } : { display: 'none', opacity: 0 }}
                transition={{
                type: "tween",
                ease: [0.28, 1.35, 1.5, .91],
                duration: 0.4
                }}
            />
            <Overlay 
                showInfo={aboutToggle}
                as={motion.div} initial={{ display: 'none', opacity: 0 }} 
                animate={aboutToggle ? { display: 'flex', opacity: 1 } : { display: 'none', opacity: 0 }}
                transition={{
                type: "tween",
                ease: [0.28, 1.35, 1.5, .91],
                duration: 0.4
                }}
            />
        </Container>
    );
};

export default CloseOverlay;
