import styled from 'styled-components'
import { Link as LinkRouter } from 'react-router-dom';
import { Link as LinkScroll } from 'react-scroll';
import { motion } from "framer-motion";

export const Container = styled(motion.div)`
    z-index: 998;
    width: 100vw;
    height: calc(100vh + 2.5px); 
    /* top: 1.75rem; */
    height: calc(100vh); 
    /* overflow-x: hidden; */
    /* -ms-overflow-style: none; */
    /* background: #FDFEF5; */
    display: block;
    /* border-bottom: 1px solid black; */
    /* grid-template-columns: 1fr 1fr; */
    position: fixed;
    /* padding-top: 6.6vh; */

    ::-webkit-scrollbar {
        display: none;
    }

    /* border-bottom: 2.5px solid #201D17; */

    overflow-y: hidden;
    overflow-x: none;
    cursor: url("/imgs/cursor1.svg"), auto;

    @media screen and (max-height: 812.5px) {
        overflow-x: hidden;
        overflow-y: hidden;
        -ms-overflow-style: none;
        border-right: 2.5px solid #201D17;
        top: calc(6vh - 2.5px);
        height: calc(90.5vh + 4px);
        width: 100vw;
    }
`
export const StartBg = styled(motion.div)`
    position: fixed;
    /* top: -101vh; */
    /* top: 0; */
    width: 100vw;
    height: 100vh;
    opacity: 1;
    background: black;
    z-index: -1;
`

export const Background = styled.div`
    width: 100vw;
    height: 100vh;
    opacity: 0.1;
    background: blue;
    position: fixed;
    /* z-index: 4;
    ${({ projDisplayed }) => projDisplayed && `
            z-index: 6;
    `} */
`

export const Loader = styled.div`
    z-index: 1;
    position: fixed;
    width: 7.7vw;
    height: 93.4vw;
    right: 0;
    top: 6.5vh;
    border-left: 1.5px solid black;
    background: #FDFEF5;
    color: black;
    font-size: 6vw;
    /* transform: rotate(90deg); */

    @media screen and (max-width: 812.5px) {
        display: none;
    }
`

export const Side = styled.div`
    z-index: 1;
    height: 7.5vw;
    width: 24.5vw;
    position: fixed;
    font-size: 8vw;
    letter-spacing: -.3vw;
    bottom: 15vh;
    left: 83.9vw;
    /* border: 1px solid black; */
    transform: rotate(90deg);

    @media screen and (max-width: 812.5px) {
        display: none;
    }    
`

export const Header = styled.div`
    position: fixed;
    background: #FDFEF5;
    /* top: 1rem; */
    z-index: 999;
    text-decoration: none;

    @media screen and (max-width: 812.5px) {
        top: 5vh;
        z-index: 11;
        padding-bottom: 9vh;
        width: 100vw;
        /* border-bottom: 1.5px solid grey; */
    }
`

export const Label = styled.div`
    display: none;
    z-index: 999;

    @media screen and (max-width: 812.5px) {
        display: grid;
        z-index: 999;
        position: fixed;
        display: flex;
        left: 0;
        height: calc(6vh - 2.5px);
        width: calc(100vw / 3);
        top: 6vh;
        /* padding-left: 18px;
        padding-top: 18px; */

        justify-content: center;
        align-items: center;
        text-align: center;

        font-size: 1.1rem;
        font-family: 'SharpGrotesk';
        /* background: #201D17; */
        /* color: #FDFEF5; */
        color: #201D17;
        border-bottom: 1.25px solid black;
        border-left: 2.5px solid black;
        border-right: 2.5px solid black;

        display: none;
    }
`

export const FiltersContainer = styled.div`
    /* left: 9vw; */
    /* top: 4.39rem; */
    /* right: calc(3.5rem - 2.5px); */
    left: calc(3.5rem - 2.5px);
    width: calc(100% - 3.5rem - 5px);
    /* width: 83.4vw; */
    position: fixed;
    top: calc(1.75rem - 2.5px);
    /* border: 1px solid black; */
    /* background: pink; */
    /* opacity: 0.3; */
    /* margin-left: -.2vw; */
    /* margin-bottom: 1vh; */
    display: flex;
    z-index: 999;

    /* overflow-x: hidden; */

    @media screen and (max-width: 812.5px) {

        /* display: block;
        display: inline;
        display: inline-block;
        display: flex;
        display: inline-flex;
        display: grid;
        display: inline-grid; */
        display: flow-root;
        /* padding-left: 0.5rem; */
        top: calc(6vh + 1rem - 2.5px);
        /* width: calc((100vw * 2) / 3); */
        width: 13%;
        left: unset;
        right: calc(.9rem);
        /* left: calc((100vw / 3)); */
        height: calc(21%);
        border-top: 2.5px solid #201D17;
        border-left: 2.5px solid #201D17;
        border-right: 2.5px solid #201D17;
        /* display: none; */
    }
`

export const Filter = styled.div`
    /* display: grid; */
    /* width: 30rem; */
    /* z-index: 4; */
    display: inline;
    width: 33.333%;
    text-align: center;
    font-size: 1.4rem;
    font-family: 'SharpGrotesk';
    letter-spacing: -.1rem;
    color: black;
    padding-left: 1.4rem;
    padding-right: 1.55rem;
    padding-top: .8rem;
    padding-bottom: .7rem;
    cursor: url("/imgs/c_sort.svg"), auto;
    /* transition: .2s all ease-in-out; */
    /* text-orientation: mixed; */
    /* writing-mode: vertical-rl; */
    /* height: 3.5vh; */
    /* border-radius: 3vh; */
    /* /* cursor: pointer; */
    /* margin-bottom: 1rem; */
    margin-right: -.5rem;
    border: 2.5px solid #201D17;

    background: ${({filterVal, fType}) => 
        filterVal === 1 & fType === 1 && 'black' || 
        filterVal === 2 & fType === 2 && 'black' ||
        filterVal === 3 & fType === 3 && 'black' || 
        filterVal !== fType && '#FDFEF5'
    };
    color: ${({filterVal, fType}) => 
        filterVal === 1 & fType === 1 && '#FDFEF5' || 
        filterVal === 2 & fType === 2 && '#FDFEF5' ||
        filterVal === 3 & fType === 3 && '#FDFEF5'
    };

    /* font-weight: bold; */
    letter-spacing: -.02rem;

    &:hover {
        /* transition: all 0.2s ease-in-out; */
        background: black;
        /* font-size: 2.8vh; */
        color: #FDFEF5;
        /* text-shadow: 0 0 1px #000; */
        /* filter: blur(2x); */
        /* -webkit-filter: blur(1.5px); */
        /* transition: 0.1s all ease-in-out; */
        /* cursor: pointer; */
    }

    @media screen and (max-width: 812.5px) {
        display: none;
    }
`

export const FilterSmall = styled.div`
    display: none;

    @media screen and (max-width: 812.5px) {
        display: flex;
        border-bottom: 2.5px solid #201D17;
        /* border-radius: 70px; */
        /* width: 33.33vw;
        height: 100%; */
        width: calc(100% / 3);
        width: 100%;
        height: calc(100% / 3);
        font-size: 1.1rem;
        font-family: 'SpaceMono';
        /* padding-left: 12.5px;
        padding-right: 12.5px;
        padding-top: 1.5vh;
        padding-bottom: 1.5vh; */
        justify-content: center;
        align-items: center;
        text-align: center;
        cursor: url("/imgs/c_sort.svg"), auto;
        /* margin-right: 6px; */

