import React, { useState, useEffect } from 'react';
import { FaBars } from 'react-icons/fa';
import { IconContext } from 'react-icons/lib';
import { Container, Nav, NavbarContainer, NavLogo, About, Logo, NavHome, Close, NavMenu, NavItem, NavLinksDOM, NavLinks, CloseBtn, NavBtnLink } from './NavbarElements';
import { motion } from "framer-motion";

const Navbar = ({mobileToggle, isOpen, toggleAbout, aboutToggle}) => {
    const [scrollNav, setScrollNav] = useState(false);

    //TODO triggers something when certain scroll level has been reached
    const changeNav = () => {
        if(window.scrollY >= 80) {
            setScrollNav(true);
        } else {
            setScrollNav(false);
        }
    };

    useEffect(() => {
        window.addEventListener('scroll', changeNav)
    }, []);

    return (
        <Container showInfo={isOpen}
            as={motion.div} initial={{ x: 0, y: '321px' }} 
            animate={isOpen ? {  x: 0, y: 0 } : { x: 0, y: '321px' }}
            transition={{
            type: "tween",
            ease: [0.28, 1.35, 1.5, .91],
            duration: 0.55
            }}
        >
            <NavMenu>
                <NavHome onClick={mobileToggle} to="/works"><Logo /><a style={{ marginLeft: '8px', marginTop: '1px' }}>호연</a></NavHome>
                <NavLinksDOM onClick={mobileToggle} to="/works">Work</NavLinksDOM>
                <NavLinksDOM onClick={mobileToggle} to="/test">The Lab</NavLinksDOM>
                <NavLinksDOM onClick={mobileToggle} to="">Photography</NavLinksDOM>
                <NavLinksDOM onClick={mobileToggle} to="/blog">Lab</NavLinksDOM>
            </NavMenu>
            <Close onClick={mobileToggle}>Close</Close>
            <About onClick={mobileToggle}>About/Contact</About>
            <CloseBtn onClick={mobileToggle}>⊠ ✕ 〤</CloseBtn>
        </Container>
    );
};

export default Navbar;
