import React, { useState } from 'react';
import { connect } from 'react-redux';
import { motion, AnimatePresence } from "framer-motion";
import SingleWork from './SingleWork';
import MobileTopButton from '../MobileTopButton';
import { Container, Background, Header, Loader, NameSort, Divider, ProjectTag, NextProjLabel, PrevProjLabel, Side, Label, StartBg, FiltersContainer, Filter, FilterSmall, ContentContainer, WorkList, ProjectItem, WorksContainer, WorkName, WorkName2, Image, Indicator, WorkItem, ContentBg, TypeSort, DateSort, WorkDate, WorkIndex, WorkType, Close, TagsContainer, TestButton, NextProj, PrevProj, AllProjs, WorksContainerMove, DetailsContainer, TagsContainer2 } from './WorkElements';
import Scrollbar from 'smooth-scrollbar';
import { FixedContent } from './WorkExpandedElements';

// const options = { 
//   damping: 0.1,
//   renderByPixels: true,
//   alwaysShowTracks: false,
//   continuousScrolling: false
// }

// Scrollbar.init(document.querySelector('#my-scrollbar'), options);

const Works = ({projects, aboutToggle}) => {

  const [filterType, setFilterType] = useState(0);
  const [projectDisplayed, setProjectDisplayed] = useState(1);
  const [hoverAllowed, setHoverAllowed] = useState(true);
  const [imageZoomed, setImageZoomed] = useState(false);

  // const [topVal, setTopVal] = useState("-1px");
  // const [leftVal, setLeftVal] = useState("-1px");

  // 100 - 4.8 = 93.2
  // 93.2 / length = * .7
  // console.log(projects.length);
  //TODO: pass in Projects Length as variable instaed of Connecting to Store to retrieve
  // let margin = (64 / projects.length) * .8;
  // let y_index = ((project.id - 1) * margin) + 49;
  // let stringY = y_index + "vh";
  // let stringY2 = (y_index - 2) + "vh";
  // console.log("stringY2: " + stringY2);

  // document.onmousemove = function(event) {
  //   // pointerX = event.screenX + "px";
  //   // pointerY = event.screenY + "px";
  //   if (event.pageY >= (window.innerHeight - 340)) {
  //       setTopVal((event.pageY - 340) + "px");
  //       setLeftVal((event.pageX + 2) + "px");
  //   } else {
  //       setTopVal((event.pageY + 1) + "px");
  //       setLeftVal((event.pageX + 2) + "px");
  //   }
  // } 

  // if ( this.state.displayQuestions ) {
  //   questions = (
  //   <div>
  //        { this.state.questions.map((question, index) => {
  //             return <Question key={question.id}
  //             title={question.title} />
  //        })}
  //   </div>
  //   )
  // }

  const toggleImgZoom = () => {
    setImageZoomed(!imageZoomed);
    // console.log("Img Toggle reached.");
    // console.log("curr zoom status: " + imageZoomed);
  };

  const isOdd = (index) => {
    if ((index % 2) == 0) {
        return true;
    } else {
        return false;
    }
  };

  const handleDisplayProject = (index) => {
    var currHoverAllowed = hoverAllowed;
    // console.log("proj: " + projectDisplayed);
    console.log("hover: " + hoverAllowed);

    if (index > 15) {
      setProjectDisplayed(1);
    } else if (index == 0) {
      setProjectDisplayed(15);
    } else if (index != -1) {
      setProjectDisplayed(index);
      setHoverAllowed(false);
      console.log("index: " + index);
    } else if (projectDisplayed == index) {
      setProjectDisplayed(-1);
      setHoverAllowed(true);
      console.log("deselecting");
    } else {
      setHoverAllowed(true);
      // setProjectDisplayed(0);
      // console.log("hover: " + hoverAllowed);
      console.log("deselecting");
    }
  };

  const togglePage = () => {
    handleDisplayProject(-1);
  };

  // ARROWS TO SWITCH BETWEEN PROJECTS, ANIMATE VERTICALLY

  function ProjTags({tag, index, tagsLength}) {    
    if (index == (tagsLength - 1)) {
      return (
        <ProjectTag><p/>{tag}. </ProjectTag>
      );
    } else {
      return (
        <ProjectTag>{tag}. </ProjectTag>
      );
    }
  };

  let project_names = projects.map((project) => {
    // means no content displayed
    var zerofilled = ('0'+project.id).slice(-2);

    const workItem = {
      hidden: { x: '0'},
      show: { X: '10vw'}
    }

    const tagsDisplayed = project.tags.map((tag, index) => (
      <ProjTags tag={tag} key={tag.id} index={index} tagsLength={project.tags.length}/>
    ));

    let index_str = 'proj' + project.id;

    if (project.id < 16) {
      if (hoverAllowed) {
        if (filterType == 0 || project.type == filterType) {
          return (
            <WorkItem onClick={() => handleDisplayProject(project.id)} filteredOut={false} id={index_str}          >
              {/* <WorkName onClick={() => handleDisplayProject(project.id)} to={100} smooth={true} duration={500} spy={true}  */}
              <WorkName>{project.name}</WorkName>
              <WorkIndex>{zerofilled}</WorkIndex> 
              <TagsContainer>{tagsDisplayed}</TagsContainer>
              <TagsContainer2>
                <WorkDate>{project.semester}</WorkDate> 
                <WorkType>{project.class}</WorkType>
              </TagsContainer2>

              <Image hoverAllowed={hoverAllowed}>
                  <img src={"imgs/" + project.imgs[0]} />
              </Image>
            </WorkItem>
          );
        } else {
          return (
            <WorkItem onClick={() => handleDisplayProject(project.id)} filteredOut={true} id={index_str}          >
              <WorkIndex>{zerofilled}</WorkIndex> 
              <TagsContainer>{tagsDisplayed}</TagsContainer>
              <TagsContainer2>
                <WorkDate>{project.semester}</WorkDate> 
                <WorkType>{project.class}</WorkType>
              </TagsContainer2>
              <WorkName>{project.name}</WorkName>

              <Image hoverAllowed={hoverAllowed}>
                  <img src={"imgs/" + project.imgs[0]} />
              </Image>
            </WorkItem>
          );
        }
      } else {
        if (filterType == 0 || project.type == filterType) {
          if (project.id == projectDisplayed) {
            return (
              <WorkItem onClick={() => handleDisplayProject(project.id)} filteredOut={false} selected={true} id={index_str}            >
                <WorkIndex>{zerofilled}</WorkIndex> 
                <TagsContainer>{tagsDisplayed}</TagsContainer>
                <TagsContainer2>
                  <WorkDate>{project.semester}</WorkDate> 
                  <WorkType>{project.class}</WorkType>
                </TagsContainer2>
                <WorkName>{project.name}</WorkName>
              </WorkItem>
            );
          } else {
            return (
              <WorkItem filteredOut={true} id={index_str}>
                <WorkIndex>{zerofilled}</WorkIndex> 
                <TagsContainer>{tagsDisplayed}</TagsContainer>
                <TagsContainer2>
                  <WorkDate>{project.semester}</WorkDate> 
                  <WorkType>{project.class}</WorkType>
                </TagsContainer2>
                <WorkName>{project.name}</WorkName>
              </WorkItem>
            );                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          
          }
        } else {
          return (
              <WorkItem filteredOut={true} id={index_str}            >
                <WorkIndex>{zerofilled}</WorkIndex> 
                <TagsContainer>{tagsDisplayed}</TagsContainer>
                <TagsContainer2>
                  <WorkDate>{project.semester}</WorkDate> 
                  <WorkType>{project.class}</WorkType>
                </TagsContainer2>
                <WorkName>{project.name}</WorkName>
              </WorkItem>
            // <WorkItem to={project.id + "/works"} filteredOut={true}><WorkName filteredOut={true} oddItem={isOdd(project.id)}>{project.name} <Image><img src={"imgs/" + project.imgs[0]} style={{height: '100%', width: '100%'}}></img></Image></WorkName></WorkItem>
          );
        }
      }
    }
  });

  let project_names_bg = projects.map((project) => {
    if (filterType == 0 || project.type == filterType) {
      return (
        <WorkItem filteredOut={false}><WorkName isBg={true} filteredOut={false} oddItem={isOdd(project.id)}>{project.name} </WorkName></WorkItem>
        );
    } else {
      return (
        <WorkItem filteredOut={true}><WorkName isBg={true} filteredOut={true} oddItem={isOdd(project.id)}>{project.name} </WorkName></WorkItem>
        );
    }
  });

  // console.log(projectDisplayed);

  function ProjContent({project}) {
    if (projectDisplayed == project.id) {
      return (
        <SingleWork project={project} selected={true} toggle={toggleImgZoom} imageZoomed={imageZoomed}></SingleWork>
      )
    } else {
      return (
        // <SingleWork project={project} selected={true}></SingleWork>
        <></>
      )
    }    
  };

  // let proj_preview = projects.map((project) => {
  //   if ((projectDisplayed + 1) > 18) {
  //     return (
  //       // <NextProj onClick={() => (handleDisplayProject(projectDisplayed + 1))}>
  //         <p>{project.name}</p>
  //       // </NextProj>
  //     );
  //   } else {
  //     return (
  //       <WorkItem filteredOut={true}><WorkName isBg={true} filteredOut={true} oddItem={isOdd(project.id)}>{project.name} </WorkName></WorkItem>
  //       );
  //   }
  // });

  // function NextProjPreview() {

  //   console.log("loprojects: " + projects.length);
  //   // console.log("proj: " + projects[1].name);

  //   // var indexChecked = index - 1;

  //   // if (index > 18) {
  //   //  indexChecked = 1;
  //   // } else if (index == 0) {
  //   //   indexChecked = 18;
  //   // }

  //   if ((projectDisplayed + 1) > 18) {
  //     return (
  //       <p>{projects[0].name}</p>
  //     );
  //   } else {
  //     return (
  //       <p>{projects[projectDisplayed + 1].name}</p>
  //     )
  //   }


  //   // var name = projects[indexChecked].name;

  //   // return (
  //     // <h1>{projects[1].name}</h1>
  //     // <p>{name}</p>
  //     // <p>hi</p>
  //   // );
  // }

  // function PrevProjPreview(index) {
  //   var indexChecked = index;

  //   if (index == 0) {
  //   }

  //   return )
  //   return (
  //     <p>{projects[indexChecked].name}</p>
  //   ); 
  // }

  let project_content = projects.map((project) => {
    return (
      <ProjContent project={project} key={project.id} />
    )
