  //   } else {
  //     return (
  //       <p>{projects[projectDisplayed + 1].name}</p>
  //     )
  //   }


  //   // var name = projects[indexChecked].name;

  //   // return (
  //     // <h1>{projects[1].name}</h1>
  //     // <p>{name}</p>
  //     // <p>hi</p>
  //   // );
  // }

  // function PrevProjPreview(index) {
  //   var indexChecked = index;

  //   if (index == 0) {
  //   }

  //   return )
  //   return (
  //     <p>{projects[indexChecked].name}</p>
  //   ); 
  // }

  let project_content = projects.map((project) => {
    return (
      <ProjContent project={project} key={project.id} />
    )
  });

  const toggleWithType = (filter) => {
    console.log("filter type: " + filterType);
    if (filterType != filter) {
      setFilterType(filter);
    } else {
      setFilterType(0);
    }
  };

  //TODO: scroll link each project title and scroll body of titles down to specific spots

  //TODO: add onClick method, toggling between T/F for state value of EACH work


  // const displayQuestion = () => {
  //   this.setState({
  //       displayQuestions: !this.state.displayQuestions
  //   })
  // }

  const pageVariants = {
    initial: {
      y: '101vh',
      // opacity: 0
    },
    in: {
      y: '0vh',
      // opacity: 1
    },
    out: {
      y: '101vh',
      // opacity: 0
    }
  }

  const filtersVariants = {
    initial: {
      y: '-10vh',
      // opacity: 0
    },
    in: {
      y: '0vh',
      // opacity: 1
    },
    out: {
      y: '-10vh',
      // opacity: 0
    }
  }

  const pageTransition = {
      type: "tween",
      ease: [0.7, 0, 0.13, 1],
      duration: 0.85
  };

  const filtersTransition = {
    type: "tween",
    ease: [0.7, 0, 0.13, 1],
    duration: 0.65
  };

  const worksVariants = {
    // transition: { staggerChildren: 0.07, delayChildren: 0.2 }
    hidden: { y: '-20vh', opacity: 0 },
    show: {
      y: '0vw', 
      opacity: 1,
      // transition: {
      //   staggerChildren: 1, delayChildren: 0.5, duration: 2
