        {/* <Loader></Loader> */}
        {/* <Side>WORKS</Side> */}
        {/* <StartBg animate={{ y: '100vh' }} initial={{ y: '-100vh' }}/> */}
        {/* <NameSort>Project</NameSort> */}
        {/* <TypeSort>Type</TypeSort> */}
        {/* <DateSort>Season</DateSort> */}
        {/* animate={hoverAllowed ? { opacity: 1, scale: 1 } : { opacity: 0 }}
          transition={{ duration: 0.4 }} */}
        <FiltersContainer as={motion.div} initial="initial"
          animate="in"
          exit="out"
          variants={filtersVariants}
          transition={filtersTransition} id="filters"
        >
            <Filter filterVal={filterType} fType={2} bgColor={'green'} onClick={() => toggleWithType(2)}>Software</Filter>
            <Filter filterVal={filterType} fType={1} bgColor={'yellow'} onClick={() => toggleWithType(1)}>Graphic Design</Filter>
            <Filter filterVal={filterType} fType={3} bgColor={'blue'} onClick={() => toggleWithType(3)}>UX Design</Filter>
            <FilterSmall filterVal={filterType} fType={2} bgColor={'green'} onClick={() => toggleWithType(2)}>SD</FilterSmall>
            <FilterSmall filterVal={filterType} fType={1} bgColor={'yellow'} onClick={() => toggleWithType(1)}>GD</FilterSmall>
            <FilterSmall filterVal={filterType} fType={3} bgColor={'blue'} onClick={() => toggleWithType(3)}>UX</FilterSmall>
        </FiltersContainer>
        <Label as={motion.div} initial="initial"
          animate="in"
          exit="out"
          variants={filtersVariants}
          transition={filtersTransition} id="filters">Filters:</Label>
        <WorksContainerMove as={motion.div} initial="initial"
          animate="in"
          exit="out"
          variants={pageVariants}
          transition={pageTransition}
        >
          <WorksContainer id="topWorks">
            {/* <Indicator>â†³</Indicator> */}
            {project_names}
          </WorksContainer>
        </WorksContainerMove>
        {/* <WorksContainer zVal={-1}>
