            type: "tween",
            ease: [0.7, 0, 0.13, 1],
            duration: 0.8 }} showContent={!hoverAllowed}
          >{project_content}
          <Close onClick={() => (handleDisplayProject(-1))}>close</Close>
        </ContentContainer>
        <ContentBg isBehind={imageZoomed} as={motion.div} initial={{ x: '-18vw' }} 
          animate={hoverAllowed ? { x: '-18vw' } : { x: '0' }}
          transition={{ duration: 0.8 }}>
            <AllProjs onClick={() => (handleDisplayProject(-1))}>
            ←
            </AllProjs>
            <NextProj onClick={() => (handleDisplayProject(projectDisplayed + 1))}>
              <NextProjLabel>Next Project
              </NextProjLabel>
              {/* <NextProjPreview /> */}
            </NextProj>
            <PrevProj onClick={() => (handleDisplayProject(projectDisplayed - 1))}>
              {/* <ProjPreview index={projectDisplayed - 1} /> */}
              <PrevProjLabel>Previous Project
              </PrevProjLabel>
            </PrevProj>
        </ContentBg>
        {/* <MobileTopButton /> */}
      </Container>
    // </AnimatePresence>
  )
};

export default connect(({projects}) => ({projects}))(Works);
