import styled from "styled-components";
import { Link as LinkRouter } from "react-router-dom";
import { Link as LinkScroll } from "react-scroll";
import { motion } from "framer-motion";

export const Container = styled(motion.div)`
  position: fixed;
  left: 0;
  // top: 103px;
  top: 0;
  z-index: 998;
  width: 100vw;
  height: calc(100vh);

  ::-webkit-scrollbar {
    display: none;
  }

  overflow-y: hidden;
  overflow-x: none;
  cursor: crosshair;
`

export const Divider = styled.div`
  z-index: 999;
  position: fixed;
  left: 30vw;
  top: 0;
  height: 100vh;

  border-left: .5px solid #403F3F;

  @media screen and (max-width: 767px) {
    display: none;
  }
`

export const WorkListContainer = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: calc(100vw);
  height: calc(100vh - 103px + 103px);
  overflow-y: scroll;
  padding-bottom: calc(51vh - 62px + 22px);
  // padding-top: calc(44vh - 94px);
  padding-top: calc(48vh - 128px - 21px + 103px);
  padding-left: 24px;
  padding-right: calc(70vw + 28px);
  z-index: 999;

  overflow-x: hidden; 

  /* background: rgb(120, 120, 120, 0.5); */
  /* background: #1E1C1F; */

  ::-webkit-scrollbar {
    display: none;
  }

  @media screen and (max-width: 767px) {
    z-index: 998;
    width: 100vw;
    padding-left: 16px;
    padding-top: calc(48vh - 128px - 16px + 103px);
    padding-bottom: calc(50vh - 62px + 42px);
  }
`;

export const Marker = styled.div`
  position: fixed;
