import styled from 'styled-components';
import { Link as LinkRouter } from 'react-router-dom';

export const Container = styled.div`
    position: fixed;
    /* height: 2em; */
    /* left: 50vw; */
    /* padding-top: 1.35rem; */
    /* padding-top: 1.75rem; */
    /* padding-left: .5rem; */
    cursor: url("/imgs/cursor1.svg"), auto;
    /* height: 4.5rem; */
    height: calc(100vh);
    height: 100vh;
    width: 3.5rem;
    left: -2.5px;
    /* left: 0; */
    /* ${({currpage}) => currpage === 'main' ? `bottom: 0` : `top: 0`}; */
    /* width: 100vw; */
    /* width: ${({currpage}) => currpage === 'lab' || 'main' ? '100vw' : '18vw'}; */
    /* width: 100vw; */
    /* background: ${({currpage}) => currpage === 'lab' ? '#FDFEF5' : 'black'}; */
    /* border-bottom: 2.5px solid #201D17; */
    border-right: 2.5px solid #201D17;
    /* font-weight: bold; */
    /* border-radius: 50px; */

    background: #FDFEF5;
    /* background: #201D17; */
    display: ${({currpage}) => currpage === 'main' && 'none'};
    /* display: flex; */
    justify-content: space-between;
    z-index: 999;
    letter-spacing: -.1rem;
    font-size: 1.6rem;
    font-family: 'SpaceMono';
    /* top: 1.5em; */
    /* margin: 1em; */
    /* font-size: 1rem; */

    text-orientation: mixed;
    writing-mode: vertical-rl;

    @media screen and (max-width: 812.5px) {
        left: 0;
        top: 0;
        width: calc(100vw);
        height: 6vh;
        /* border-top: 1.5px solid #201D17;
        border-left: 1.5px solid #201D17;
        border-right: 2.5px solid #201D17; */
        background: none;
        text-orientation: unset;
        writing-mode: horizontal-tb;
        font-size: 1.1rem;
        letter-spacing: -.04rem;
        /* background: black; */
    }
`;

export const Controller = styled.div`
    z-index: 999;
    position: fixed;
    left: 50vw;
    left: 30%;
    top: 45vh;
    /* top: 45%; */
    /* transform: translate(-50%, -50%); */

    border-radius: 3rem;
    width: 2rem;
    height: 2rem;
    background: red;
    /* border: 1rem solid #FDFEF5; */

    &:hover {
        /* border: 1rem solid red; */
        /* transition: .2s all ease-in-out; */
        opacity: 0.5;
        transform: translate(-50%, -50%), scale(1.5);
        /* cursor: pointer; */
    }
`
// export const Kim = styled.span`
//     font-family: 'CG Italic';
//     font-size: 1.39rem;
//     /* color: black; */
//     letter-spacing: -.15rem;
//     padding-right: 0.2rem;
//     /* display: inline; */
// `

export const NavHome = styled(LinkRouter)`
    display: relative;
    padding-top: 1.5rem;
    padding-bottom: 1.5rem;
    width: 100%;
    /* border-bottom: 2.5px solid #201D17; */
    padding-right: .35rem;

    /* height: 100%; */
    /* font-size: 2rem; */
    /* opacity: 0.8; */
    color: #201D17;
    flex: 1;
    /* background-image: linear-gradient(to right, black 0, black 100%);
    background-position: 0 1.15em;
    background-size: 0 100%;
    background-repeat: no-repeat; */
    cursor: url("/imgs/cursor1.svg"), auto;
    border-bottom: 2.5px solid #201D17;

    text-decoration: none;
    /* position: absolute; */
    /* left: .875rem; */
    /* letter-spacing: -.1rem; */
    /* font-family: 'CG Medium Italic'; */
    font-family: 'SpaceMono';

    color: ${({currpage, thispage}) => 
        currpage === thispage && '#FDFEF5'
    };

    background: ${({currpage, thispage}) => 
        currpage === thispage && '#201D17;'
    };

    /* ${({ currpage }) => currpage === 'lab' && `
        color: white;
    `} */
/* 
    display: ${({currpage, thispage}) => 
        currpage === thispage && 'none' ||
        currpage === 'main' && 'none'
    }; */

    /* transition: all .1s; */

    &:hover {
        /* border: .4em solid blue; */
        /* border: 2.5px solid black; */
        /* text-shadow: 0 0 1px #000; */
        /* filter: blur(1.5x); */
        /* -webkit-filter: blur(1.5px); */
        cursor: url("/imgs/cursor1.svg"), auto;
        background: #201D17;
        color: #FDFEF5;
        /* transition: 0.2s all ease-in-out; */
        /* background: ${({ currpage }) => (currpage === 'lab' ? 'white' : 'black')}; */
        /* color: ${({ currpage }) => (currpage === 'lab' ? 'white' : 'black')}; */
        /* background-size: 100% 100%; */
    }

    @media screen and (max-width: 812.5px) {
        /* color: white; */
        position: relative;
        display: flex;
        /* left: 0; */
        /* top: 0; */
        width: calc(100% / 3);
        height: 100%;
        /* padding-top: -1vh; */
        /* height: calc(3vh - 5px); */
        /* padding-bottom: 1.5rem; */
        /* padding-top: 1rem; */
        /* padding-right: .45rem; */
        /* padding: auto; */
        border-bottom: 0;
        padding: 0;
        justify-content: center;
        align-items: center;
        text-align: center;
        /* margin-left: 16px; */
        /* padding-left: 0; */
        /* left: 12.5px; */
    }
`;

export const NonMobile = styled.ul`
    display: flex;
    /* align-items: center; */
    /* list-style: none; */
    /* text-align: right; */
    position: fixed;
    left: 0rem;
    /* left: .875rem; */
    /* top: 14.5rem; */
    width: 3.5rem;
    /* top: calc(100vh - 1rem); */
    cursor: url("/imgs/c_right.svg"), auto;
    bottom: 0;
    /* justify-content: space-between; */
    /* right: 1vw; */
    /* height: 100%; */

    /* margin-right: -21.5px; */
    @media screen and (max-height: 812.5px) {
        display: none;
    }
`;

export const NonMobile1 = styled.ul`
    display: flex;
    /* align-items: center; */
    /* list-style: none; */
    /* text-align: right; */
    position: fixed;
    left: 0rem;
    /* left: .875rem; */
    /* top: 14.5rem; */
    width: 3.5rem;
    /* top: calc(100vh - 1rem); */
    /* top: 1.75rem; */
    /* justify-content: space-between; */
    /* right: 1vw; */
    /* height: 100%; */

    /* margin-right: -21.5px; */

    @media screen and (max-height: 812.5px) {
        top: 0;
        width: calc((100vw * 2) / 3);
        width: 100vw;
        height: 6vh;
        overflow-y: hidden;
        right: 0;
        left: 0;
        border: 2.5px solid #201D17;
        background: #FDFEF5;
    }
`;

export const NavDOMLink = styled(LinkRouter)`
    padding-bottom: 1.5rem;
    padding-top: 1.5rem;
    padding-right: .45rem;
    width: 100%;
    color: #201D17;
    font-family: 'SpaceMono';
    display: flex;
    align-items: left;
    text-decoration: none;
    /* letter-spacing: -.1rem; */
    border-bottom: 2.5px solid #201D17;

    color: ${({currpage, thispage}) => 
        currpage === thispage && '#FDFEF5'
    };

    background: ${({currpage, thispage}) => 
        currpage === thispage && '#201D17;'
    };

    /* transition: all .2s ease-in-out; */

    &:hover {
        cursor: url("/imgs/cursor1.svg"), auto;
        background: #201D17;
        color: #FDFEF5;
    }

    @media screen and (max-height: 812.5px) {
        border-bottom: 0;
        padding: 0;
        width: calc(100vw / 3);
        justify-content: center;
        align-items: center;
        text-align: center;
        border-top: 0;
        border-left: 2.5px solid #201D17;
    }
`;

export const NavDOMLinkInfo = styled(LinkRouter)`
    padding-bottom: 1.5rem;
    padding-top: 1.5rem;
    padding-right: .45rem;
    width: 100%;
    color: #201D17;
    display: flex;
    font-family: 'SpaceMono';
    align-items: left;
    text-decoration: none;
    /* letter-spacing: -.1rem; */
    border-top: 2.5px solid #201D17;

    /* cursor: url("/imgs/c_right.svg"), auto; */


    color: ${({currpage, thispage}) => 
        currpage === thispage && '#FDFEF5'
    };

    background: ${({currpage, thispage}) => 
        currpage === thispage && '#201D17;'
    };

    /* transition: all .2s ease-in-out; */

    &:hover {
        cursor: url("/imgs/cursor1.svg"), auto;
        background: #201D17;
        color: #FDFEF5; 
    }
`;

export const AboutContainer = styled.div`
    z-index: 998;
    /* display: none; */
    display: ${({ showInfo }) => (showInfo ? 'block' : 'none')};
    width: 32.5vw;
    height: 101vh;
    background: white;
`

export const InfoWrapper = styled.span`
    z-index: 999;
`

export const Mobile = styled.div`
    display: none;
    position: fixed;

    @media screen and (max-height: 530px) {
        z-index: 998;
        display: block;
        position: fixed;
        bottom: 0rem;
        left: 0;
        background: transparent;
        font-size: 5vh;
        /* cursor: pointer; */
        background: ${({toggle}) => (toggle ? 'white' : 'black')};
        background: white;
        color: ${({toggle}) => (toggle ? 'black' : 'black')};
        padding-left: 2.5vw;
        padding-top: 4px;
        border: 1.5px solid black;
        height: 6vh;
        /* border-radius:50%; */
        width: 11vw;
/* 
        &:hover {
            color: white;
            background-color: black;
            transition: all 0.3s ease-in-out;
        } */
    }
`;

export const Rotating = styled.div`
    width: 100%;
    height: 100%;
    transition: 0.3s all ease-in-out;
    transform: ${({toggle}) => (toggle ? 'rotate(0.5turn)' : 'rotate(0turn)')};
`
