    let totalImgs = work.content.length;
    let thumbHeight = ((36 / totalImgs) - 1) + "vh";
    // console.log("each thumb height: " + thumbHeight);
    // height -> 36 / work

    console.log("imgs: " + work.content);

    const imgVariants = {
        initial: {
            opacity: 0,
            y: "100vh",
            scale: 0.9,
        },
        in: {
            opacity: 1,
