                    </Overview>
                </Header>
            );
        } else {
            if (picture.includes("/")) {
                return (
                    <PicContainer id={index_str}
                        index={index}
                        isLast={is_last}
                        as={motion.div}
                        initial="initial"
                        animate="in"
                        exit="out"
                        variants={imgVariants}
                        transition={{
                            type: "tween",
