                    isLast={is_last}
                    as={motion.div}
                    initial="initial"
                    animate="in"
                    exit="out"
                    variants={imgVariants}
                    transition={{
                        type: "tween",
                        ease: [0.7, 0, 0.13, 1],
                        duration: 0.85,
                        delay: (index + 1) * 0.1,
                    }}
