        initial: {
            opacity: 0,
            y: '0s',
            x: '1.5rem',
            // y: "-100vh",
            scale: 0.9,
        },
        in: {
            opacity: 1,
            x: 0,
            // y: '2.5rem',
            scale: 1,
        },
        out: {
            opacity: 0,
            // y: "-100vh",
            scale: 1,
        },
    };

    const leftTransition = {
        type: "tween",
        ease: [0.7, 0, 0.13, 1],
        duration: 0.6,
    }

    const rightTransition = {
        type: "tween",
        ease: [0.7, 0, 0.13, 1],
        duration: 0.85,
    }

    return (
        <Container id='head'
            // as={motion.div}
            // initial="initial"
            // animate="in"
            // exit="out"
        >
            {/* <LeftSection
                // as={motion.div}
                // initial="initial"
                // animate="in"
                // exit="out"
