import React, { useState } from 'react';
import { motion } from "framer-motion";
import { useLocation } from 'react-router-dom';
import AboutPanel from '../AboutPanel';
import { Container, NavContainer, Logo, NavHome, NonMobile, NonMobile1, PhotoIndexContainer, NavDOMLink, NavDOMLinkAbout, InfoLink, Mobile, AboutContainer, Kim, InfoWrapper, Rotating, FiltersContainer, Filter} from './NavigationElements';

const Navigation = ({toggle, aboutToggle}) => {

    const location = useLocation();

    let currPage = location.pathname.split("/").pop();

    const [showInfo, setShowInfo] = useState(false);
    const [filterType, setFilterType] = useState(0);

    const toggleShowInfo = () => {
        var tempShowInfo = showInfo;
        setShowInfo(!tempShowInfo);
        console.log("Toggle Info: " + showInfo);
    };

    // function checkAbout() {
    //     if (aboutToggle) {
    //         {toggle};
    //     }
    // }

    // const toggleDotOpened = () => {
    //     var tempShowInfo = showInfo;
    //     setDotOpened(!dotOpened);
    //     console.log("dot open status" + dotOpened);
    // };

    //TODO: Cases for when clicking on other tabs or things UNDER Info Card
    const pageTransition = {
        type: "tween",
        ease: [0.7, 0.1, 0.4, 1],
        duration: 0.5
    };

    let infoDrop = null;

    if (showInfo) {
        infoDrop = (
            <AboutContainer as={motion.div} initial={{ scale: 0.9, opacity: 0.2 }} 
                            animate={{ scale: 1, opacity: 1}} exit={{ scale: 0.9, opacity: 0.2 }}
                            transition={{ type: "tween", ease: [0.87, 0, 0.13, 1], duration: 0.7 }}>
                <AboutPanel />
            </AboutContainer>
        )
    }

    const filtersVariants = {
        initial: {
          x: "-12rem",
          // opacity: 0
        },
        in: {
          x: "0vh",
          // opacity: 1
        },
        out: {
          x: "-12rem",
          transition: {
              type: "tween",
              ease: [0.7, 0, 0.13, 1],
              duration: 0.85,
          }
          // opacity: 0
        },
      };

    const toggleWithType = (filter) => {
        console.log("filter type: " + filterType);
        if (filterType != filter) {
        setFilterType(filter);
        } else {
        setFilterType(0);
        }
    };

    return (
        // <Container currPage={currPage} as={motion.div} initial={{ x: '0', y: '0' }} 
        //     animate={aboutToggle ? {  x: '25vw', y: 0 } : { x: '0', y: '0' }}
        //     transition={pageTransition}>
        <Container>
            <AboutContainer as={motion.div} initial={{ x: '20.5rem', y: '105vh' }} 
                            animate={showInfo ? {  x: '20.5rem', y: -18 } : { x: '20.5rem', y: '-105vh' }}
                            transition={pageTransition} showInfo={showInfo}>
                <AboutPanel />
            </AboutContainer>
            {/* <NavContainer> */}
            <NonMobile>
                <Logo />
                <NavHome currpage={currPage} thispage={'works'} to="/works">호연</NavHome>
            </NonMobile>
            <NonMobile1>
                <NavDOMLink currpage={currPage}>Click me for fun</NavDOMLink>
                {/* <NavDOMLink currpage={currPage} thispage={'main'} to="/">Blog</NavDOMLink> */}
                <NavDOMLinkAbout currpage={aboutToggle} thispage={true} onClick={() => toggle('info')}>About</NavDOMLinkAbout>
                <NavDOMLink currpage={currPage}>Get in touch</NavDOMLink>
            </NonMobile1>
            {/* </NavContainer> */}
            <FiltersContainer 
                as={motion.div} 
                initial="initial"
                animate="in"
                exit="out"
                variants={filtersVariants}
                transition={{
                    type: "tween",
                    ease: [0.7, 0, 0.13, 1],
                    duration: 0.8,
                    delay: 1 * 0.12,
                }}
            >
                <Filter 
                    as={motion.div} 
                    initial="initial"
                    animate="in"
                    exit="out"
                    variants={filtersVariants}
                    transition={{
                        type: "tween",
                        ease: [0.7, 0, 0.13, 1],
                        duration: 0.8,
                        delay: 2 * 0.12,
                    }}
                    filterVal={filterType} 
                    fType={2} 
                    bgColor={'green'} 
                    onClick={() => toggleWithType(2)}
                    >
                        Case Studies
                </Filter>
                <Filter 
                    as={motion.div} 
                    initial="initial"
                    animate="in"
                    exit="out"
                    variants={filtersVariants}
                    transition={{
                        type: "tween",
                        ease: [0.7, 0, 0.13, 1],
                        duration: 0.8,
                        delay: 3 * 0.12,
                    }}
                    filterVal={filterType} 
                    fType={1} bgColor={'yellow'} 
                    onClick={() => toggleWithType(1)}
                >
                    Photography
                </Filter>
                <Filter 
                    as={motion.div} 
                    initial="initial"
                    animate="in"
                    exit="out"
                    variants={filtersVariants}
                    transition={{
                        type: "tween",
                        ease: [0.7, 0, 0.13, 1],
                        duration: 0.8,
                        delay: 4 * 0.12,
                    }}
                    filterVal={filterType} 
                    fType={3} 
                    bgColor={'blue'} 
                    onClick={() => toggleWithType(3)}
                >
                    Projects
                </Filter>
                <Filter 
                    as={motion.div} 
                    initial="initial"
                    animate="in"
                    exit="out"
                    variants={filtersVariants}
                    transition={{
                        type: "tween",
                        ease: [0.7, 0, 0.13, 1],
                        duration: 0.8,
                        delay: 4 * 0.12,
                    }}
                    filterVal={filterType} 
                    fType={4} 
                    bgColor={'pink'} 
                    onClick={() => toggleWithType(3)}
                    >
                    Blog
                </Filter>
            </FiltersContainer>
            <PhotoIndexContainer><p>small photo index here</p></PhotoIndexContainer>
            {/* <Mobile onClick={toggle}><Rotating>↑</Rotating></Mobile> */}
        </Container>
    );
};

export default Navigation;
