import styled from "styled-components";
import { keyframes } from "styled-components";

export const Container = styled.div`
    overflow: hidden;
    background: #FDFEF5;
    height: 100vh;
    width: 2.4rem;
    width: 3.5rem;
    z-index: 998;
    /* border-bottom: 1.5px solid black; */
    border-left: 2.5px solid black;
    /* border-top: 1.5px solid black; */
    display: flex;
    position: fixed;
    bottom: 0;
    right: 0;
    overflow-x: hidden;
    cursor: url("/imgs/cursor1.svg"), auto;

    /* &:before {
        background-color: rgba(255,255,255,0.5);
        backdrop-filter: blur(10px) saturate(100%) contrast(45%) brightness(130%);
        -webkit-backdrop-filter: blur(10px) saturate(100%) contrast(45%) brightness(130%);
        content: "";
        height: 100%;
        position: absolute;
        width: 100%;
    } */

    @media screen and (max-width: 768px) {
        left: 0;
        width: 100vw;
        height: calc(3.5vh + 2.5px);
        bottom: 0;
        overflow-x: hidden;     
        border-right: 2.5px solid black;
        border-bottom: 2.5px solid black;
        border-top: 2.5px solid black;

        display: none;
        opacity: 0;
    }
`

export const HoverContainer = styled.div`
    /* position: fixed;
    display: none;
    left: 50%;
    top: 50%;
    transform: translate(-50%, -50%);
    width: 20vw;
    height: 20vw;
    background: green; */

    position: absolute;
    white-space: nowrap;
    will-change: transform;
    animation: marquee 10s linear infinite;
`


const linearText1 = keyframes`
    from {
        transform: translateY(100%);
    }
    to {
        transform: translateY(-100%);
    };
` 

const marquee1 = keyframes`
    0% {
        top: 100%;
    }
    100% {
        top: -100%
    }
`

const marquee2 = keyframes`
    0% {
        left: 100%;
    }
    100% {
        left: -150%
    }
`

export const Marquee = styled.span`
    cursor: url("/imgs/cursor1.svg"), auto;

    writing-mode: vertical-rl;
    text-orientation: mixed;

    font-family: 'SpaceMono';
    letter-spacing: -.05rem;
    white-space: nowrap;
    font-size: 1.2rem;
    font-size: 1.575rem;
    color: black;

    margin-right: .35rem;
    display: inline;
    position: fixed;
    right: 0;
    top: 100vh;

    /* padding-top: ${({starter}) => (starter ? '0' : '10px')}; */
    /* animation: ${({starter}) => (starter ? linearText1 : linearText2)} 10s linear infinite; */
    animation: ${marquee1} 20s linear infinite;
    /* animation: ${linearText1} 10s linear infinite; */
    /* animation: marquee1 30s linear infininte; */
    animation-delay: ${({starter}) => (starter ? '0s' : '20s')};


    @media screen and (max-width: 768px) {
        /* top: 0; */
        top: calc(96vh + .4rem);
        /* margin-top: 0; */
        margin-right: unset;
        bottom: 0;
        height: 100%;
        width: 100vw;
        /* margin-left: 0rem; */
        right: unset;
        left: 100vw;
        /* left: -50vw; */
        postion: fixed;
        display: grid;

        writing-mode: horizontal-tb;
        text-orientation: mixed;
        font-size: .85rem; /* right: 0; */

        /* padding-left: ${({starter}) => (starter ? '0' : '10px')}; */
        /* letter-spacing: -.01rem; */
        animation: ${marquee2} 15s linear infinite;
        /* animation: ${({starter}) => (starter ? linearText3 : linearText4)} 20s linear infinite; */
        /* animation-delay: ${({starter}) => (starter ? '0s' : '15s')}; */
    }
`

const linearText2 = keyframes`
    from {
        transform: translateY(0%);
    }
    to {
        transform: translateY(-200%);
    };
` 

const linearText3 = keyframes`
    from {
        transform: translateX(-100%);
    }
    to {
        transform: translateX(100%);
    };
` 

const linearText4 = keyframes`
    from {
        transform: translateX(-200%);
    }
    to {
        transform: translateX(0%);
    };
`
