import React, { useState } from 'react';
import { motion } from "framer-motion";
import { useLocation } from 'react-router-dom';
import About from '../About';
import { Container, NavHome, NonMobile, NonMobile1, NavDOMLink, NavDOMLinkInfo, InfoLink, Mobile, AboutContainer, Kim, InfoWrapper, Controller, Rotating} from './NavigationElements';
import Status from '../Status';

const Navigation = ({toggle, aboutToggle}) => {

    const location = useLocation();

    let currPage = location.pathname.split("/").pop();

    const [showInfo, setShowInfo] = useState(false);
    const [dotOpened, setDotOpened] = useState(false);

    const toggleShowInfo = () => {
        var tempShowInfo = showInfo;
        setShowInfo(!tempShowInfo);
        console.log("Toggle Info: " + showInfo);
    };

    // function checkAbout() {
    //     if (aboutToggle) {
    //         {toggle};
    //     }
    // }

    const toggleDotOpened = () => {
        var tempShowInfo = showInfo;
        setDotOpened(!dotOpened);
        console.log("dot open status" + dotOpened);
    };

    //TODO: Cases for when clicking on other tabs or things UNDER Info Card
    const pageTransition = {
        type: "tween",
        ease: [0.7, 0.1, 0.4, 1],
        duration: 0.5
    };

    let infoDrop = null;

    if (showInfo) {
        infoDrop = (
            <AboutContainer as={motion.div} initial={{ scale: 0.9, opacity: 0.2 }} 
                            animate={{ scale: 1, opacity: 1}} exit={{ scale: 0.9, opacity: 0.2 }}
                            transition={{ type: "tween", ease: [0.87, 0, 0.13, 1], duration: 0.7 }}>
                <About />
            </AboutContainer>
        )
    }

    return (
        <Container currPage={currPage} as={motion.div} initial={{ x: '0', y: '0' }} 
            animate={aboutToggle ? {  x: '25vw', y: 0 } : { x: '0', y: '0' }}
            transition={pageTransition}>
            {/* <Status /> */}
            {/* <AboutContainer as={motion.div} initial={{ x: '20.5rem', y: '105vh' }} 
                            animate={showInfo ? {  x: '20.5rem', y: -18 } : { x: '20.5rem', y: '-105vh' }}
                            transition={pageTransition} showInfo={showInfo}>
                <About />
            </AboutContainer> */}
            {/* <Controller onClick={() => (toggleDotOpened())}></Controller> */}
            {/* <NavDOMLink2
                as={motion.div}
                initial={{ opacity: 0, x: '50vw', y: '45vh' }} 
                animate={dotOpened ? { opacity: 1, x: 'calc(50vw + 3rem)', y: '45vh' } : {opacity: 0, x: '50vw', y: '45vh'}}
                transition={{ type: "tween", ease: [0.87, 0, 0.13, 1], duration: 1 }} currpage={currPage} thispage={'lab'} to="/5/works">
                lab
            </NavDOMLink2> */}

            <NonMobile1>
                <NavHome currpage={currPage} thispage={''} to="/" onClick={() => toggle('f')}>A. Kim</NavHome>
                <NavDOMLink currpage={currPage} thispage={'works'} to="/works" onClick={() => toggle('f')}>Index</NavDOMLink>
                <NavDOMLink currpage={currPage} thispage={'lab'} to="/lab" onClick={() => toggle('f')}>Lab</NavDOMLink>    
            </NonMobile1>
            <NonMobile>
                <NavDOMLinkInfo currpage={aboutToggle} thispage={true} onClick={() => toggle('info')} style={{cursor: 'url("/imgs/cursor1.svg"), auto'}}>Info</NavDOMLinkInfo>
                {/* <NavDOMLink currpage={currPage} thispage={'info'} onClick={() => toggleShowInfo()}>info</NavDOMLink> */}
            </NonMobile>
            {/* <Mobile onClick={toggle}><Rotating>â†‘</Rotating></Mobile> */}
        </Container>
    );
};

export default Navigation;
