                            ease: [0.7, 0, 0.13, 1],
                            duration: 0.85,
                            delay: (index + 1) * 0.1,
                        }}
                        >
                        <img src={"/imgs/" + picture} alt={picture} id="project-img"></img>   
                    </PicContainer>
                );
            } else if (picture.startsWith('SS:')) {
                let header_name = picture.slice('3');

                return (
                    <Subsection ref={myPicRefs[index]}>
                        {header_name}
                    </Subsection>
                )
            } else {
               return (
                   <PicDesc
                    index={index}
                    // isLast={is_last}
                    as={motion.div}
                    initial="initial"
                    animate="in"
                    exit="out"
                    variants={imgVariants}
                    transition={{
                        type: "tween",
                        ease: [0.7, 0, 0.13, 1],
                        duration: 0.85,
                        delay: (index + 1) * 0.1,
                    }}
