                   </PicDesc>
               )
           }
        }
    }


    function ListofTOC({subsection, index}) {
        if (subsection.startsWith('SS:')) {
            setShowTOC(true);

            let header_name = subsection.slice(3);

            return (
                <TOCItem onClick={()=> handleClick(index)}>{header_name}</TOCItem>
            )
        } else if (subsection.startsWith('SS2:')) {
            let sub_name = subsection.slice(4);

            return (
                <TOCItem2 onClick={()=> handleClick(index)}>{sub_name}</TOCItem2>
            )
        } else {
            return (
                <></>
            )
        }
    }

    const updateCurrImage = (img_index) => {
        // console.log("curr img index: " + img_index);
        if (currImage != img_index) {
            setCurrImage(currImage);
        }
    };

    function Tags({tag}) {
        return (
            <Tag>{tag}</Tag>
        )
    };

    const pictures_displayed = work.imgs.map((picture, index) => (
        <Picture_Displayed picture={picture} key={picture.id} index={index} />
    ));

    const table_of_contents = work.imgs.map((subsection, index) => (
        // <Get_TOC subsection={subsection} key={subsection.id} index={index}/>
        <ListofTOC subsection={subsection} key={subsection.id} index={index} />
        // <TOCItem>{subsection}</TOCItem>
    ));

    const leftVariants = {
        initial: {
            opacity: 0,
            y: '0',
            // y: "-100vh",
            scale: 0.9,
        },
        in: {
            opacity: 1,
            // y: '2.5rem',
            scale: 1,
        },
        out: {
            opacity: 0,
            // y: "-100vh",
            scale: 1,
        },
    };

    const controlVariants = {
        initial: {
            opacity: 0,
            y: '0s',
            x: '1.5rem',
            // y: "-100vh",
            scale: 0.9,
        },
        in: {
            opacity: 1,
            x: 0,
            // y: '2.5rem',
            scale: 1,
        },
        out: {
            opacity: 0,
            // y: "-100vh",
            scale: 1,
        },
    };

    const leftTransition = {
        type: "tween",
        ease: [0.7, 0, 0.13, 1],
        duration: 0.6,
    }

    const rightTransition = {
        type: "tween",
        ease: [0.7, 0, 0.13, 1],
        duration: 0.85,
    }


    return (
        <Container id='head'
            // as={motion.div}
            // initial="initial"
            // animate="in"
            // exit="out"
        >
            <RightSection
                as={motion.div}
                initial={{ y: '100vh' }}
                animate={{ y: 0 }}
                exit={{ y: '-100vh', transition: {
                    type: "tween",
                    ease: [0.7, 0, 0.13, 1],
                    duration: .7}
                }}
                transition={{
                    type: "tween",
                    ease: [0.7, 0, 0.13, 1],
                    duration: 1.2,
                }}
            >
                {pictures_displayed}
            </RightSection>

            <Controls
                as={motion.div}
                initial={{ x: '-100vw' }}
                animate={{ x: 0 }}
                exit={{ x: '-100vw' }}
                transition={{
                    type: "tween",
                    ease: [0.7, 0, 0.13, 1],
                    duration: 0.7,
                }}
            >
                <Back to="/works">All Works</Back>
                <TableOfContents>
                    {/* <TOCItem style={{ fontSize: '20px' }}>Study Overview</TOCItem> */}
                    <p style={{ paddingBottom: '6px' }}> {showTOC ? 'Study Overview' : ''}</p>
                    {table_of_contents}
                </TableOfContents>
                <Back href={"https://www.junehomes.com"} target="_blank">Visit</Back>
            </Controls>

            <MobileTOC
                as={motion.div} initial={{ x: 0, y: 'calc(100vh - 90px)' }} 
                animate={mobileTOC ? {  x: 0, y: '90px' } : { x: 0, y: 'calc(100vh - 90px)' }}
                transition={{
                type: "tween",
                ease: [0.28, 1.35, 1.5, .91],
                duration: 0.35
                }}
            >
                <button onClick={toggleMobileTOC}>test up</button>
            </MobileTOC>
            {/* <MobileTopButton /> */}
        </Container>
    );
};

export default connect(({ projects }) => ({ projects }))(WorkExpanded);
